import React from 'react'
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import { Link } from "react-router-dom";
import { useState , useEffect } from "react";
import { useAuthContext } from "../components/hooks/useAuthContext";





var data = [{}];
data = JSON.parse(localStorage.getItem("user"));
const id = data._id;
function Search() {
    const { user } = useAuthContext();
    const [bookings, setBookings] = useState([])
    useEffect (() => {
        const fetchBookings = async () => {
            const response = await fetch('api/bookings/', {
                headers: { Authorization: `Bearer ${user.token}` },
              });
            const json = await response.json()

            if(response.ok){
                setBookings(json)
            }
        }
        fetchBookings()
    }, [])

    const [searchTerm,setsearchTerm] = useState('');
    const [value,setValue]=useState(''); // selected value in filter

    const handleSelect=(e)=>{
    console.log(e,id);
    setValue(e)
                            }
    const handleSubmit=(e)=>{
    e.preventDefault();
    // const display = bookings.filter((post) => post._id[0])
    console.log(searchTerm);
    setsearchTerm("");
    
                            }                       
                          
  
  return (
    <div>
      
      {bookings && bookings.map((workout) => (
        <p key={workout._id}>{workout}</p>
      ))}
      <InputGroup className="mb-3">
        <form onSubmit={handleSubmit}>
          <input
            aria-label="Text input with dropdown button"
            placeholder="Search"

            onChange={(event) => {setsearchTerm(event.target.value); console.log(searchTerm)}}
          />
         
          <DropdownButton
            variant="secondary"
            title="Filter by"
            id="input-group-dropdown-2"
            align="end"
            onSelect={handleSelect}
          >
            <Dropdown.Item eventKey="brand" >
              Brand
            </Dropdown.Item>
            <Dropdown.Divider />
            <Dropdown.Item eventKey="stats" >
              Status
            </Dropdown.Item>
          </DropdownButton>
          </form>
        </InputGroup>
        
                 
            
        {console.log(value,id)}

    </div>
  )
}

export default Search
