import React from 'react'
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import { Link } from "react-router-dom";
import { useState , useEffect } from "react";
import { useAuthContext } from "../components/hooks/useAuthContext";



function Searchv2() {
    
    const [data,setData] = useState([]);
    useEffect (() => {
        const data = JSON.parse(localStorage.getItem("user"));
        if (data) {
            setData(data);
          }
      
    },[])
    const id = data._id;
    const { user } = useAuthContext();
    const [bookings, setBookings] = useState([])
    useEffect (() => {
        const fetchBookings = async () => {
            const response = await fetch('/api/bookings', {
                headers: { Authorization: `Bearer ${user.token}` },
              });
            const json = await response.json()

            if(response.ok){
                setBookings(json)
            }
        }
        fetchBookings()
    },[])
   
    const [searchTerm,setsearchTerm] = useState('');
    const [value,setValue]=useState({filter: value}); // selected value in filter
  
    const handleSelect=(e)=>{
  
    console.log(e,id);
    setValue(e)
    bookings.push(value)
    console.log(bookings.filter)
                     }
    
    const handleSubmit=(e)=>{
    e.preventDefault();
    setsearchTerm("");
    }
    
    
    
    
                                         
                          
  
  return (
    <div>
      
      <form onSubmit={handleSubmit}>
      <InputGroup className="mb-3">
        
          <input
            aria-label="Text input with dropdown button"
            placeholder="Search"

            onChange={(event) => {setsearchTerm(event.target.value); console.log(searchTerm)}}
          />
         
          <DropdownButton
            variant="secondary"
            title="Filter by"
            id="input-group-dropdown-2"
            align="end"
            onSelect={handleSelect}
          >
            <Dropdown.Item eventKey="brand" >
              Brand
            </Dropdown.Item>
            <Dropdown.Divider />
            <Dropdown.Item eventKey="stats" >
              Status
            </Dropdown.Item>
          </DropdownButton>
         
        </InputGroup>
        </form>
        {bookings.filter((val) => {
        
        if (searchTerm == ""){
	    return val
	    }else if(val.filter.toLowerCase().includes(searchTerm.toLowerCase())){
	    return val
        }}).map((val) => (
        <p key={val._id}>{val.stats}</p>
      ))}        
            
       

    </div>
  )
}

export default Searchv2
